import React from 'react';
import { StyleSheet, Image, Dimensions, Text, View } from 'react-native';
import topo from './../../assets/topo.png';

const width = Dimensions.get('screen').width;

export default function Cesta(){
    return <>
                <Image source={topo} style={estilos.topo}></Image>
                <Text style={estilos.titulo}>Detalhes da cesta</Text>

                <View style={estilos.cesta__container}>
                    <Text style={estilos.nome__cesta}>Cesta de Verduras</Text>
                    <Text style={estilos.nome__fazenda}>Jenny Jack Farm</Text>
                    <Text style={estilos.nome__descricao}>Uma cesta com produtos selecionados
                          cuidadosamente da fazenda direto para
                          sua cozinha
                    </Text>
                    <Text style={estilos.preco}>R$ 100,00</Text>
                </View>
            </>
}

const estilos = StyleSheet.create({
    topo: {
        width: "100%",
        height: 578 / 768 * width,
    },
    titulo: {
        position: "absolute",
        fontWeight: "bold",
        marginTop: 140,
        fontSize: 30,
        color: "#00d33e"
    },
    cesta__container: {
        paddingVertical: 8,
        paddingHorizontal: 16,
    },  
    nome__cesta: {
        color: "#8cd448",
        fontSize: 30,
        fontWeight: "bold",
        marginBottom: 10,
    },
    nome__fazenda: {
        fontSize: 18,
        lineHeight: 26,
        fontWeight: "bold"
    },
    nome__descricao: {
        color: "#A3A3A3",
        fontSize: 16,
        lineHeight: 26,
        textAlign: "justify",
        paddingVertical: 8,
        paddingHorizontal: 20
    },
    preco: {
        color: "#8cd448",
        fontWeight: "bold",
        fontSize: 26
    }
});

